window.d3 = {
  select: function(selector) {
    const elem = document.querySelector(selector);
    return {
      append: function(tag) {
        const child = document.createElement(tag);
        if (elem) elem.appendChild(child);
        return this;
      },
      attr: function(name, value) {
        if (elem && value !== undefined) elem.setAttribute(name, value);
        return this;
      },
      style: function(name, value) {
        if (elem && value !== undefined) elem.style[name] = value;
        return this;
      },
      selectAll: function() { return this; },
      data: function() { return this; },
      enter: function() { return this; },
      exit: function() { return this; },
      remove: function() { return this; }
    };
  },
  tree: function() {
    return {
      size: function() { return this; },
      separation: function() { return this; }
    };
  },
  hierarchy: function(data) {
    return {
      descendants: function() { return []; },
      links: function() { return []; }
    };
  },
  linkHorizontal: function() {
    return {
      x: function() { return this; },
      y: function() { return this; }
    };
  }
};
